			pathString += "C 80 " + y + " 90 " + y + " 90 " + (y + 10);
			pathString += "L 90 " + (y + 20);
			pathString += "C 90 " + (y + 20) + " 90 " + (y + 30) + " 80 " + (y + 30);
			pathString += "L 20 " + (y + 30);

			pathString += "L 10 " + (y + 40);
			pathString += "L 10 " + (y + 10);
			pathString += "C 10 " + (y + 10) + " 10 " + y + " 20 " + y;
			
