				pathString += "L " + (x + 90) + " " + (y + 80);
				pathString += "C " + (x + 90) + " " + (y + 80) + " " + (x + 90) + " " + (y + 90) + " " + (x + 80) + " " + (y + 90);
				pathString += "L " + (x + 10) + " " + (y + 90);
				
				pathString += "L " + x + " " + (y + 100);
				pathString += "L " + x + " " + (y + 10);
				pathString += "C " + x + " " + (y + 10) + " " + x + " " + y + " " + (x + 10) + " " + y;
			
